这是对 CharacterController.Move 中示例的补充，添加了跳跃功能。 由于使用 Move 而不是 SimpleMove ，所以需要自己处理重力。 using System;
using UnityEngine; [RequireComponent(typeof(CharacterController))]
public class ExampleScript : MonoBehaviour
{ public float moveSpeed = 2.0f; // 移动速度 public float gravity = 9.8f; // 重力大小 public float jumpHeight = 2f; // 跳跃高度 public float sensitivity = 2f; // 鼠标灵敏度 private CharacterController _controller; private float _ySpeed; // Y 轴速度 private void PlayerRotate() { var mouseX = Input.GetAxis("Mouse X") * sensitivity; transform.Rotate(Vector3.up * mouseX); } private void PlayerMove() { var motion = transform.right * Input.GetAxis("Horizontal") + transform.forward * Input.GetAxis("Vertical"); motion *= moveSpeed; // 跳跃 switch (_controller.isGrounded) { case true when Input.GetKeyDown(KeyCode.Space): _ySpeed = (float) Math.Sqrt(2 * gravity * jumpHeight); break; case true: _ySpeed = 0; break; case false: _ySpeed -= gravity * Time.deltaTime; // 重力 break; } motion += transform.up * _ySpeed; _controller.Move(motion * Time.deltaTime); } private void Start() { _controller = GetComponent<CharacterController>(); } private void Update() { PlayerRotate(); PlayerMove(); }
}

